perspectives.style:
@controls {
  bottom {
    filter {
      target: element;
      by: "element type";
      as: labels;
      multiple: true;
      default: show-all;
    }
  }
}

@settings {
  template: stakeholder;
  direct-decorations: true;
  theme: dark;
  layout-preset: hairball;
  font-size: 57;
  layout: force;
  connection-size: 12;
  connection-curvature: 0.14;
  element-size: 55;
  element-text-align: bottom;
  element-color: categorize("Element Type", paired);
  ignore-orphans: true;
}

person {
  icon: user solid;
}

project {
  icon: hornbill;
}

database {
  icon: dice;
}

service {
  icon: concierge-bell;
}

tools {
  icon: wrench;
}

element["label"="ANAC"] {
  icon: plane;
  icon-color: white;
}

element["status"="Bad"] {
  shadow-color: #c6c6c6;
  shadow-opacity: 0.3;
  shadow-size: 2;
}

/* org: headOf (http://www.w3.org/ns/org#headOf) */
connection["connection type"="gerente de área"], connection["connection type"="gerente de projeto"], connection["connection type"="Curador de dados"] {
  size: 24;
  style: dashed;
}

element {
  popover: **"{{label}}"**<br/>"{{description}}";
}

